import { useState, useEffect } from 'react';
import InfiniteScroll from 'react-infinite-scroll-component';
import { useTransactions } from '../hooks/useTransactions';
import TransactionItem from '../components/explorer/TransactionItem';
import { useSearchParams } from 'react-router-dom';

const Explorer = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const search = searchParams.get('search') || '';
  const category = searchParams.get('category') || 'All';

  const { transactions, loadTransactions, refresh, loading, hasMore } = useTransactions();

  useEffect(() => {
    refresh();
  }, [search, category]);

  const filters = { search, category };

  return (
    <div>
      <input 
        placeholder="Search title" 
        value={search} 
        onChange={(e) => setSearchParams({ search: e.target.value, category })}
      />
      <select value={category} onChange={(e) => setSearchParams({ search, category: e.target.value })}>
        <option>All</option>
        <option>Food</option>
        <option>Rent</option>
      </select>
      <InfiniteScroll
        dataLength={transactions.length}
        next={() => loadTransactions(filters)}
        hasMore={hasMore}
        loader={<h4>Loading...</h4>}
      >
        {transactions.map(t => <TransactionItem key={t._id} transaction={t} />)}
      </InfiniteScroll>
      {transactions.length === 0 && <p>No transactions found</p>}
    </div>
  );
};

export default Explorer;
